import { Injectable } from '@angular/core';
import { Course } from '../model/course';
import { HttpClient } from '@angular/common/http'

//Service utilizado para filtrar json
//Aqui se obtem as informações do servidor

/*O decorador também é responsável por habilitar a injeção de dependência na classe de serviço.  */
@Injectable({
  providedIn: 'root'
})
export class CoursesService {

  //Chamada assícrona pro servidor com httpClient
  // O HttpClient é um serviço do Angular que permite fazer requisições HTTP
  // para servidores remotos. Para usá-lo, você deve injetá-lo no componente ou serviço onde deseja usá-lo e, em seguida,
  //  chamar seus métodos para enviar requisições.
  //Ele é chamado automaticamente quando uma instância da classe é criada e é usado para inicializar os membros da classe
  //e executar outras tarefas de inicialização  o constructor é usado principalmente para injetar dependências em um componente ou serviço, usando POO
  constructor(private HttpClient: HttpClient) { }

  list(): Course[] {
    return [
      { _id: "1", name: "Angular", category: "Front-End"}
    ]
  }
}
