import { Injectable } from '@angular/core';
import { Course } from '../model/course';
import { HttpClient } from '@angular/common/http'
import { first, tap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CoursesService {

  //Criar variável de ação como adicionar por exemplo com o caminho de chamar
  private readonly API = "/assets/courses.json"

  constructor(private HttpClient: HttpClient) { }

  list() {
    //Um observable que retorna um array de Course com o generics <Course[]>
    return this.HttpClient.get<Course[]>(this.API)

    //Métodos do rxjs
    //método pipe é usado para gerar uma sequência de informações de dados com
    .pipe(
      //Não deixar a conexão aberta,
      first(),
     /*O método "tap" no Angular é uma função disponível no objeto Observable que permite executar uma ação intermediária
     em uma cadeia de operações de observação. A função "tap" permite inspecionar os valores de um fluxo de dados sem alterá-los,
     o que pode ser útil para depuração, registro ou para realizar ações secundárias.  */
      tap(courses => console.log(courses))
    );
  }
}
